  }



  getData() {
    axios.get('http://localhost:3001/todo/tasks')
      .then(response => {
        console.log('data response', response);
         let completeData =[];
        response.data.data.forEach(element => {

          completeData.push(element)


        });
        this.setState({
          Data: completeData,

        })
      })
      .catch(error => {
        console.log('data error', error);
      })
  }
  getComponent() {
    if (this.state.answer) {  // show the modal if state showModal is true
      return <BlogDetail/>;
    } else {
      return null;
    }
  }




  render() {

    return (
      <div>
        <h1>
          Blog Titles.
      </h1>
        <h2>
        {
            this.state.Data.map((item,index)=> (
              <p>
                <a onClick = {this.clicked.bind(this,item)}> 
                {item.title}
                </a>
              </p>
            ))
         }
         </h2>
      </div>



    );
  }
}
