import React, { Component } from 'react';
import axios from 'axios';

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      data: {}
    }
    this.getData = this.getData.bind(this);
    this.sendData = this.sendData.bind(this);
    this.getData();
  }
  getData() {
    axios.get('http://localhost:3001/todo/tasks')
    .then(response => {
      console.log('data response', response);
      this.setState({
        data: response.data
      })
    })
    .catch(error => {
      console.log('data error', error);
    })
  }
  render() {
    return(
      <div>
      <h1>
      This is my blog. 
      </h1>
      <p>
      {this.state.data.status} 
      </p>
      <form onSubmit={(e) => {this.sendData(e.target.value); e.preventDefault();}}>
        <label>
        Title:
        <input onChange={this.handleTitle} type="text" name="name" />
        </label>
        <br></br>
        <label>
        Body:
        <input onChange={this.handleBody} type="text" name="name" size="100" />
        </label>
        <input type="submit"  value="Submit" />
      </form>
      </div>
      );
  }
}
