import React, { Component } from 'react';
import axios from 'axios';

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      title: '',
      body: '',
      data: {}
    }
    this.getData = this.getData.bind(this);
    this.sendData = this.sendData.bind(this);
    this.handleTitle = this.handleTitle.bind(this);
    this.handleBody = this.handleBody.bind(this);
    this.getData();
  }
  sendData() {
    axios.post('http://localhost:3001/todo/create', {body: {
      title: this.state.title,
      data: this.state.body
    }}, { })
    .then(response => {
      console.log('post response', response)
    })
    .catch(error => {
      console.log('post error', error);
    })
  }
  handleTitle(e) {
    this.setState({
      title: e.target.value
    })
  }
  handleBody(e) {
    this.setState({
      body: e.target.value
    })
  }
  getData() {
    axios.get('http://localhost:3001/todo/tasks')
      .then(response => {
        console.log('data response', response);
        this.setState({
          data: response.data
        })
      })
      .catch(error => {
        console.log('data error', error);
      })
  }
  render() {
    return (
      <div>
        <h1>
          This is my blog.
      </h1>
        <p>
          {this.state.data.status}
        </p>
        <form  onSubmit={(e) => {this.sendData(); e.preventDefault()}}>
          <label>
            Title:
            <input onChange={this.handleTitle.bind(this)} type="text" name="title" />
          </label>
          <br></br>
          <label>
            Body:
            <input onChange={this.handleBody.bind(this)} type="text" name="body" size="100" />
          </label>
          <input type="submit" value="Submit" />
        </form>
        {this.state.body}
        {this.state.title}
      </div>
    );
  }
}
