import React, { Component } from 'react';
import axios from 'axios';

import Show from './Show';

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      title: [],
      data: []
    }
    this.getData = this.getData.bind(this);
    this.getData();
  }

  clicked(){
    console('')
  }



  getData() {
    axios.get('http://localhost:3001/todo/tasks')
      .then(response => {
        console.log('data response', response);
        let blogData = [], blogTitle = [];
        response.data.data.forEach(element => {
          // console.log(element.data)
          blogTitle.push(element.title)
          blogData.push(element.data)
        });
        this.setState({
          data: blogData,
          title: blogTitle
        })
      })
      .catch(error => {
        console.log('data error', error);
      })
  }
  render() {
    const body = [], titles = [];

    for (const [index, value] of this.state.data.entries()) {
      body.push(<li key={index}>{value}</li>)
    }
    for (const [index, value] of this.state.title.entries()) {
      titles.push(<li key={index}>{value}</li>)
    }
    return (
      <div>
        <h1>
          This is my blog.
      </h1>
        {titles}
        <button onClick = {(e)=> this.clicked}> View Body</button>
      </div>
    );
  }
}
